tables.table_schema:
CREATE TABLE articles (
    article_id INT PRIMARY KEY,
    title VARCHAR(255),
    content TEXT,
    author_id INT
);

CREATE TABLE images (
    image_id INT PRIMARY KEY,
    image_url VARCHAR(255),
    description TEXT,
    uploader_id INT
);

CREATE TABLE videos (
    video_id INT PRIMARY KEY,
    video_url VARCHAR(255),
    title VARCHAR(255),
    author_id INT
);
