tables.table_schema:
CREATE TABLE CampaignTags (
    tag_id INT PRIMARY KEY,
    campaign_id INT,
    tag_name TEXT,
    PRIMARY KEY (tag_id, campaign_id)
);

CREATE TABLE CampaignContacts (
	contact_id UUID PRIMARY KEY,
	campaign_id UUID REFERENCES EmailCampaigns(campaign_id) ON DELETE CASCADE,
	contact_name VARCHAR(255) NOT NULL,
	email_address VARCHAR(255) NOT NULL
);

CREATE TABLE CampaignAudience (
	audience_id UUID PRIMARY KEY,
	campaign_id UUID REFERENCES EmailCampaigns(campaign_id) ON DELETE CASCADE,
	segment_name VARCHAR(255) NOT NULL,
	total_contacts INT
);
