tables.table_schema:
CREATE TABLE customers (
    id INT PRIMARY KEY,
    name VARCHAR(255),
    email VARCHAR(255),
    preferences JSON
);

CREATE TABLE marketing_content (
    content_id INT PRIMARY KEY,
    content_title VARCHAR(255),
    content_text TEXT
);

CREATE TABLE products (
    id INT PRIMARY KEY,
    product_name VARCHAR(255),
    category VARCHAR(50),
    price DECIMAL(10, 2)
);
