tables.table_schema:
CREATE TABLE social_media_engagement_metrics (
    post_id INT,
    likes INT,
    shares INT,
    comments INT
);

CREATE TABLE marketing_strategies (
    strategy_id INT,
    strategy_name VARCHAR(50),
    audience_segment VARCHAR(50),
    success_metric VARCHAR(50)
);

CREATE TABLE posts (
    post_id INT PRIMARY KEY,
    user_id INT,
    content TEXT,
    post_date TIMESTAMP
);
