tables.table_schema:
CREATE TABLE CompetitorCompany (
    company_id UUID PRIMARY KEY,
    company_name VARCHAR NOT NULL,
    industry VARCHAR,
    location VARCHAR
);

CREATE TABLE ad_copy (
    ad_id INT,
    campaign_id INT,
    ad_text TEXT,
    ad_image_url VARCHAR(255)
);

CREATE TABLE marketing_channels (id SERIAL PRIMARY KEY,
  channel_name VARCHAR(255) NOT NULL,
  platform VARCHAR(50) NOT NULL);
