CREATE TABLE CampaignPerformance (
  campaign_id INT PRIMARY KEY,
  reach INT,
  clicks INT,
  conversions INT
);

CREATE TABLE CampaignDetails (
  campaign_id INT PRIMARY KEY,
  campaign_name VARCHAR(255),
  start_date DATE,
  end_date DATE
);

CREATE TABLE Reach (
  campaign_id INT NOT NULL,
  impressions INT NOT NULL,
  date DATE NOT NULL
);

CREATE TABLE Clicks (
  click_id INT NOT NULL,
  campaign_id INT NOT NULL,
  clicks INT NOT NULL,
  date DATE NOT NULL
);

CREATE TABLE Conversions (
  conversion_id INT NOT NULL,
  campaign_id INT NOT NULL,
  conversions INT NOT NULL,
  date DATE NOT NULL
);

