tables.table_schema:
CREATE TABLE influencers (
  id INT PRIMARY KEY,
  name TEXT,
  followers INT,
  engagement_rate FLOAT
);

CREATE TABLE Influencers (
    influencer_id INT PRIMARY KEY,
    influencer_name VARCHAR(255),
    followers_count INT,
    category VARCHAR(50)
);

CREATE TABLE influencers (
    influencer_id INT PRIMARY KEY,
    influencer_name VARCHAR(255) NOT NULL,
    followers_count INT,
    category VARCHAR(50)
);

CREATE TABLE recommended_influencers (
    influencer_id INT PRIMARY KEY,
    score DECIMAL,
    FOREIGN KEY (influencer_id) REFERENCES influencers(influencer_id)
);

CREATE TABLE influencers (
  id INT PRIMARY KEY,
  name VARCHAR,
  followers INT,
  genre VARCHAR
);

CREATE TABLE campaigns (
  campaign_id INT PRIMARY KEY,
  campaign_name VARCHAR,
  start_date DATE,
  end_date DATE
);

CREATE TABLE campaign_influencers (
  campaign_id INT,
  influencer_id INT,
  engagement_rate FLOAT,
  PRIMARY KEY (campaign_id, influencer_id)
);

CREATE TABLE influencer_ratings (
  influencer_id INT,
  rating FLOAT,
  rating_date DATE,
  PRIMARY KEY (influencer_id, rating_date)
);

CREATE TABLE influencer_engagement (
  influencer_id INT,
  engagement_date DATE,
  engagement_rate FLOAT,
  PRIMARY KEY (influencer_id, engagement_date)
);
