tables.table_schema:
CREATE TABLE Influencers (
  influencer_id SERIAL PRIMARY KEY,
  influencer_name VARCHAR(50) NOT NULL,
  payment_amount DECIMAL(10, 2) NOT NULL,
  payment_date DATE
);

CREATE TABLE Budgets (
  budget_id SERIAL PRIMARY KEY,
  budget_name VARCHAR(50) NOT NULL,
  allocated_amount DECIMAL(10, 2) NOT NULL,
  remaining_amount DECIMAL(10, 2) NOT NULL
);

CREATE TABLE influencer_payments (
  id SERIAL PRIMARY KEY,
  influencer_id INT,
  payment_date DATE,
  payment_amount DECIMAL(10, 2)
);

CREATE TABLE influencer_details (
  influencer_id SERIAL PRIMARY KEY,
  influencer_name VARCHAR(50),
  influencer_email VARCHAR(100),
  influencer_social_media VARCHAR(50)
);

CREATE TABLE Influencers (
    influencer_id INT PRIMARY KEY,
    influencer_name VARCHAR(50) NOT NULL,
    follower_count INT,
    payment_amount DECIMAL(10, 2)
);

CREATE TABLE Expenses (
    expense_id INT PRIMARY KEY,
    expense_date DATE,
    description TEXT,
    amount DECIMAL(10, 2)
);
