CREATE TABLE content_performance (
    content_id UUID PRIMARY KEY,
    views INT,
    likes INT,
    comments INT
);

CREATE TABLE content_metadata (
    content_id UUID PRIMARY KEY,
    title VARCHAR,
    author VARCHAR,
    date_published DATE
);

CREATE TABLE views (
    id INT PRIMARY KEY,
    content_id INT,
    views_count INT
);

CREATE TABLE likes (
    id INT PRIMARY KEY,
    content_id INT,
    likes_count INT
);

CREATE TABLE comments (
    id INT PRIMARY KEY,
    content_id INT,
    comment_text TEXT
);

CREATE TABLE views (
    id UUID PRIMARY KEY,
    content_id UUID,
    user_id UUID,
    view_timestamp TIMESTAMP
);

CREATE TABLE likes (
    id UUID PRIMARY KEY,
    content_id UUID,
    user_id UUID,
