);

CREATE TABLE content (
    id UUID PRIMARY KEY,
    title VARCHAR(255),
    url VARCHAR(255),
    created_at TIMESTAMP
);

CREATE TABLE content_views (id INT PRIMARY KEY,
content_id INT,
views INT);

