tables.table_schema:
CREATE TABLE message_content (
    message_id SERIAL PRIMARY KEY,
    content TEXT NOT NULL
);

CREATE TABLE abusive_keywords (
    keyword_id SERIAL PRIMARY KEY,
    keyword TEXT NOT NULL
);

CREATE TABLE message_content_analysis (
    id INT PRIMARY KEY,
    message TEXT,
    is_abusive BOOLEAN
);

CREATE TABLE abusive_words (
    word_id INT PRIMARY KEY,
    word TEXT
);

CREATE TABLE message_word_mapping (
    message_id INT,
    word_id INT,
    FOREIGN KEY (message_id) REFERENCES message_content_analysis(id),
    FOREIGN KEY (word_id) REFERENCES abusive_words(word_id)
);

CREATE TABLE Messages (
    message_id INT PRIMARY KEY,
    message_text TEXT NOT NULL,
    sender_id INT NOT NULL,
    timestamp TIMESTAMP NOT NULL
);

CREATE TABLE Users (
    user_id INT PRIMARY KEY,
    username VARCHAR(50) UNIQUE NOT NULL,
    email VARCHAR(100) UNIQUE NOT NULL
);

CREATE TABLE BlacklistedWords (
    word_id INT PRIMARY KEY,
    word VARCHAR(50) NOT NULL
);
