tables.table_schema:
CREATE TABLE banned_users ( 
    user_id INT PRIMARY KEY, 
    username VARCHAR(50) NOT NULL, 
    ban_reason TEXT, 
    banned_at TIMESTAMP NOT NULL DEFAULT now() 
);

CREATE TABLE banned_users (
 id INT PRIMARY KEY,
 username VARCHAR(50) NOT NULL,
 reason TEXT
 );

CREATE TABLE banned_ips (
 ip_address VARCHAR(15) PRIMARY KEY,
 user_id INT,
 ban_time TIMESTAMP
 );

CREATE TABLE blacklist_users (
    user_id SERIAL PRIMARY KEY,
    username VARCHAR(50) NOT NULL,
    email VARCHAR(100) NOT NULL UNIQUE,
    reason TEXT NOT NULL
);

CREATE TABLE banned_domains (
    domain_id SERIAL PRIMARY KEY,
    domain_name VARCHAR(100) NOT NULL,
    description TEXT
);

CREATE TABLE banned_ips (
    ip_id SERIAL PRIMARY KEY,
    ip_address INET NOT NULL,
    reason TEXT
);

CREATE TABLE users (
    user_id SERIAL PRIMARY KEY,
    username VARCHAR(50) NOT NULL,
    email VARCHAR(100) UNIQUE NOT NULL,
    online_behavior VARCHAR(255) NOT NULL
);

CREATE TABLE banned_users (
    banned_user_id SERIAL PRIMARY KEY,
    user_id INT REFERENCES users(user_id) ON DELETE CASCADE,
    reason VARCHAR(255) NOT NULL,
    ban_date TIMESTAMP NOT NULL
);

CREATE TABLE banned_ips (
    ip_id SERIAL PRIMARY KEY,
    ip_address VARCHAR(50) UNIQUE NOT NULL,
    reason VARCHAR(255) NOT NULL,
    ban_date TIMESTAMP NOT NULL
);

CREATE TABLE ban_reports (
    report_id SERIAL PRIMARY KEY,
    user_id INT REFERENCES users(user_id) ON DELETE SET NULL,
    ip_id INT REFERENCES banned_ips(ip_id) ON DELETE CASCADE,
    report_date TIMESTAMP NOT NULL
);

CREATE TABLE user_activity (
    activity_id INT PRIMARY KEY,
    user_id INT NOT NULL,
    activity_date DATE NOT NULL,
    activity_type VARCHAR(50) NOT NULL
);

CREATE TABLE ban_reasons (
    reason_id INT PRIMARY KEY,
    reason_description TEXT NOT NULL
);

CREATE TABLE ban_logs (
    log_id INT PRIMARY KEY,
    user_id INT NOT NULL,
    reason_id INT NOT NULL,
    ban_date DATETIME NOT NULL
);

CREATE TABLE banned_users (
    user_id INT PRIMARY KEY,
    ban_start_date DATETIME NOT NULL,
    ban_end_date DATETIME
);
