tables.table_schema:
CREATE TABLE cyberbullying_incidents (
    incident_id INT PRIMARY KEY,
    victim_name VARCHAR(50),
    bully_name VARCHAR(50),
    incident_date DATE
);

CREATE TABLE evidence_attachments (
    attachment_id INT PRIMARY KEY,
    report_id INT NOT NULL,
    attachment_url VARCHAR(255) NOT NULL,
    attachment_type VARCHAR(50) NOT NULL
);

CREATE TABLE report_status (
    status_id INT PRIMARY KEY,
    report_id INT NOT NULL,
    status VARCHAR(50) NOT NULL,
    updated_at TIMESTAMP NOT NULL
);

CREATE TABLE evidence_of_incidents (
	evidence_id INT PRIMARY KEY,
	incident_date DATE,
	evidence_details TEXT
);

CREATE TABLE reporter_details (
	reporter_id INT PRIMARY KEY,
	reporter_name VARCHAR(50),
	reporter_email VARCHAR(100),
	reporter_phone VARCHAR(15)
);

CREATE TABLE reported_person (
	person_id INT PRIMARY KEY,
	person_name VARCHAR(50),
	person_age INT,
	person_gender VARCHAR(10)
);

CREATE TABLE cyberbullying_categories (
	category_id INT PRIMARY KEY,
	category_name VARCHAR(50),
	category_description TEXT
);
