tables.table_schema:
CREATE TABLE user_privacy_settings (
	user_id INT PRIMARY KEY,
	data_sharing_enabled BOOLEAN NOT NULL,
	data_deletion_requested BOOLEAN,
	last_access_timestamp TIMESTAMP
);

CREATE TABLE users (
    user_id INT PRIMARY KEY,
    username VARCHAR(50) NOT NULL,
    email VARCHAR(100) UNIQUE NOT NULL,
    password_hash VARCHAR(200) NOT NULL
);

CREATE TABLE permissions (
    permission_id INT PRIMARY KEY,
    permission_name VARCHAR(50) NOT NULL
);

CREATE TABLE user_permissions (
    user_id INT REFERENCES users(user_id),
    permission_id INT REFERENCES permissions(permission_id)
);

CREATE TABLE data_logs (
    log_id INT PRIMARY KEY,
    user_id INT REFERENCES users(user_id),
    log_message TEXT NOT NULL,
    log_timestamp TIMESTAMP NOT NULL
);

CREATE TABLE data_preferences (
    preference_id INT PRIMARY KEY,
    user_id INT,
    data_type VARCHAR(50),
    permission_level INT
);

CREATE TABLE access_logs (
    log_id INT PRIMARY KEY,
    user_id INT,
    timestamp TIMESTAMP,
    action VARCHAR(50)
);

CREATE TABLE data_controls (
    control_id INT PRIMARY KEY,
    user_id INT,
    data_type VARCHAR(50),
    control_type VARCHAR(50)
);

CREATE TABLE data_audit (
    audit_id INT PRIMARY KEY,
    user_id INT,
    data_type VARCHAR(50),
    audit_details VARCHAR(255)
);
