  storage_location VARCHAR(255),
  data_type VARCHAR(255),
  storage_capacity INT
);

CREATE TABLE impact_assessment (
    id INT PRIMARY KEY,
    assessment_name VARCHAR(255),
    data_collection_method VARCHAR(255),
    assessment_date DATE
);

CREATE TABLE privacy_risks (
    id INT PRIMARY KEY,
    risk_name VARCHAR(255),
    risk_description TEXT,
    risk_rating INT
);

CREATE TABLE data_storage (
    id INT PRIMARY KEY,
    storage_type VARCHAR(255),
    storage_capacity INT,
    storage_location VARCHAR(255)
);

CREATE TABLE impact_assessments (
    assessment_id SERIAL PRIMARY KEY,
    data_collection_method VARCHAR(100) NOT NULL,
    assessment_date DATE NOT NULL,
    privacy_risk_level INT
);

CREATE TABLE data_collectors (
    collector_id SERIAL PRIMARY KEY,
    collector_name VARCHAR(100) NOT NULL,
    collector_email VARCHAR(255) UNIQUE
);

CREATE TABLE data_sources (
    source_id SERIAL PRIMARY KEY,
    source_name VARCHAR(100) NOT NULL,
    data_type VARCHAR(50) NOT NULL
);

CREATE TABLE assessment_results (
    result_id SERIAL PRIMARY KEY,
    assessment_id INT REFERENCES impact_assessments(assessment_id),
    data_source_id INT REFERENCES data_sources(source_id),
    risk_score INT
);

CREATE TABLE impact_assessment (
    id INT PRIMARY KEY,
    name VARCHAR(255) NOT NULL,
    description TEXT,
    date_conducted DATE
);

CREATE TABLE data_collection (
    id INT PRIMARY KEY,
    data_type VARCHAR(50) NOT NULL,
    collection_method VARCHAR(50) NOT NULL,
    collection_date TIMESTAMP
);

CREATE TABLE data_storage (
    id INT PRIMARY KEY,
    storage_location VARCHAR(100) NOT NULL,
    storage_capacity INT,
    encryption_method VARCHAR(50)
);

CREATE TABLE privacy_risks (
    id INT PRIMARY KEY,
    risk_description TEXT,
    likelihood_of_risk INT,
    impact_level INT
);

CREATE TABLE assessment_results (
    id INT PRIMARY KEY,
    assessment_id INT,
    risk_id INT,
    risk_score INT
);
