tables.table_schema:
CREATE TABLE post_metrics (
  post_id INT,
  total_likes INT,
  total_shares INT,
  total_comments INT
);

CREATE TABLE Post (
  post_id INT PRIMARY KEY,
  post_content TEXT
);

CREATE TABLE users (
    user_id INT PRIMARY KEY,
    username TEXT,
    email TEXT
);

CREATE TABLE users (
    user_id UUID PRIMARY KEY,
    name TEXT,
    email TEXT
);

CREATE TABLE user_likes (
    like_id UUID PRIMARY KEY,
    user_id UUID,
    post_id UUID,
    timestamp TIMESTAMP
);

CREATE TABLE user_shares (
    share_id UUID PRIMARY KEY,
    user_id UUID,
    post_id UUID,
    timestamp TIMESTAMP
);
