tables.table_schema:
CREATE TABLE comments (
    comment_id INT PRIMARY KEY,
    user_id INT,
    post_id INT,
    comment_text TEXT
);

CREATE TABLE users (
    user_id INT PRIMARY KEY,
    user_name VARCHAR(50),
    user_email VARCHAR(100)
);

CREATE TABLE posts (
    post_id INT PRIMARY KEY,
    post_text TEXT,
    post_date TIMESTAMP
);

CREATE TABLE user_comments (
  comment_id INT PRIMARY KEY,
  user_id INT,
  comment_text TEXT,
  sentiment_score FLOAT
);

CREATE TABLE social_media_posts (
  post_id INT PRIMARY KEY,
  user_id INT,
  post_content TEXT,
  post_date TIMESTAMP
);

CREATE TABLE user_sentiments (
  sentiment_id INT PRIMARY KEY,
  user_id INT,
  sentiment_score FLOAT
);

CREATE TABLE sentiment_analysis (
  analysis_id INT PRIMARY KEY,
  comment_id INT,
  post_id INT,
  analysis_result TEXT
);

CREATE TABLE UserSentiments (
    user_id INT PRIMARY KEY,
    comment TEXT,
    sentiment_score FLOAT
);

CREATE TABLE PostSentiments (
    post_id INT PRIMARY KEY,
    post_text TEXT,
    sentiment_score FLOAT
);

CREATE TABLE UserComments (
    comment_id INT PRIMARY KEY,
    user_id INT,
    post_id INT,
    comment_text TEXT
);

CREATE TABLE PostFeedback (
    feedback_id INT PRIMARY KEY,
    post_id INT,
    user_id INT,
    feedback_text TEXT
);

CREATE TABLE PostStatistics (
    post_id INT PRIMARY KEY,
    comments_count INT,
    likes_count INT,
    shares_count INT
);
