tables.table_schema:
CREATE TABLE trending_topics (
    topic_id INT PRIMARY KEY,
    topic_name VARCHAR(255) NOT NULL,
    topic_hashtag VARCHAR(50) NOT NULL
);

CREATE TABLE user_generated_content (
    content_id INT PRIMARY KEY,
    content TEXT NOT NULL,
    topic_id INT,
    FOREIGN KEY (topic_id) REFERENCES trending_topics(topic_id)
);

CREATE TABLE trending_topics (
    id INT PRIMARY KEY,
    topic_name VARCHAR(255) NOT NULL,
    hashtag VARCHAR(50) NOT NULL,
    tweet_count INT NOT NULL
);

CREATE TABLE user_tweets (
    tweet_id INT PRIMARY KEY,
    user_id INT NOT NULL,
    tweet_content TEXT NOT NULL,
    timestamp TIMESTAMP NOT NULL
);

CREATE TABLE user_followers (
    user_id INT PRIMARY KEY,
    follower_id INT NOT NULL
);

CREATE TABLE hashtags (
    hashtag_id INT PRIMARY KEY,
    hashtag_name VARCHAR(255) NOT NULL
);

CREATE TABLE users (
    user_id INT PRIMARY KEY,
    username VARCHAR(255) NOT NULL,
    email VARCHAR(255) NOT NULL
);

CREATE TABLE posts (
    post_id INT PRIMARY KEY,
    user_id INT REFERENCES users(user_id),
    post_content TEXT NOT NULL,
    post_date TIMESTAMP
);

CREATE TABLE post_hashtags (
    post_id INT REFERENCES posts(post_id),
    hashtag_id INT REFERENCES hashtags(hashtag_id)
);

CREATE TABLE trending_topics (
    topic_id INT PRIMARY KEY,
    topic_name VARCHAR(255) NOT NULL,
    trend_date TIMESTAMP
);
