tables.table_schema:
CREATE TABLE SocialMediaAdvertisingCampaignPerformance (
    campaign_id INT PRIMARY KEY,
    campaign_name TEXT,
    click_through_rate FLOAT,
    conversion_rate FLOAT
);

CREATE TABLE SocialMediaCampaigns (
    campaign_id UUID PRIMARY KEY,
    campaign_name VARCHAR(255),
    click_through_rate FLOAT,
    conversion_rate FLOAT
);

CREATE TABLE social_media_campaigns (
    campaign_id UUID PRIMARY KEY,
    campaign_name STRING,
    start_date DATE,
    end_date DATE
);

CREATE TABLE click_through_rates (
    click_id UUID PRIMARY KEY,
    campaign_id UUID REFERENCES social_media_campaigns(campaign_id),
    click_date TIMESTAMP,
    click_count INT
);

CREATE TABLE conversion_metrics (
    conversion_id UUID PRIMARY KEY,
    click_id UUID REFERENCES click_through_rates(click_id),
    conversion_date TIMESTAMP,
    conversion_count INT
);

CREATE TABLE social_media_campaigns (
    campaign_id INT PRIMARY KEY,
    campaign_name TEXT,
    start_date DATE,
    end_date DATE
);

CREATE TABLE click_through_rates (
    click_id INT PRIMARY KEY,
    campaign_id INT,
    click_time TIMESTAMP,
    user_id INT
);

CREATE TABLE conversion_metrics (
    conversion_id INT PRIMARY KEY,
    campaign_id INT,
    conversion_time TIMESTAMP,
    user_id INT
);

CREATE TABLE users (
    user_id INT PRIMARY KEY,
    username TEXT,
    email TEXT
);

CREATE TABLE advertising_campaigns (
    campaign_id INT PRIMARY KEY,
    campaign_name VARCHAR(255),
    start_date DATE,
    end_date DATE
);

CREATE TABLE social_media_accounts (
    account_id INT PRIMARY KEY,
    account_name VARCHAR(255)
);

CREATE TABLE advertising_accounts (
    account_id INT PRIMARY KEY,
    account_type VARCHAR(255),
    social_media_account_id INT,
    FOREIGN KEY (social_media_account_id) REFERENCES social_media_accounts(account_id)
);
