tables.table_schema:
CREATE TABLE BrandAwareness (
    id INT PRIMARY KEY,
    campaign_id INT,
    mentions VARCHAR,
    shares INT
);

CREATE TABLE Campaigns (
    campaign_id INT PRIMARY KEY,
    campaign_name VARCHAR,
    start_date DATE,
    end_date DATE
);

CREATE TABLE Reputation (
    id INT PRIMARY KEY,
    brand_id INT,
    reputation_score INT,
    comments TEXT
);

CREATE TABLE Campaigns (
    campaign_id INT PRIMARY KEY,
    campaign_name TEXT,
    start_date DATE,
    end_date DATE
);

CREATE TABLE campaigns (
    id SERIAL PRIMARY KEY,
    campaign_name VARCHAR(100) NOT NULL,
    start_date DATE,
    end_date DATE
);

CREATE TABLE users (
    id SERIAL PRIMARY KEY,
    username VARCHAR(50) UNIQUE NOT NULL,
    full_name VARCHAR(100),
    email VARCHAR(100)
);

CREATE TABLE brands (
    id SERIAL PRIMARY KEY,
    brand_name VARCHAR(100) UNIQUE NOT NULL,
    industry VARCHAR(50)
);
