tables.table_schema:
CREATE TABLE voter_information (
    voter_id INT PRIMARY KEY,
    voter_name VARCHAR(50),
    voter_age INT,
    voter_party_affiliation VARCHAR(50)
);

CREATE TABLE advertising_campaigns (
    campaign_id INT PRIMARY KEY,
    campaign_name VARCHAR(50),
    campaign_budget DECIMAL(10, 2),
    target_location VARCHAR(50)
);
