tables.table_schema:
CREATE TABLE social_media_platform (
    platform_id SERIAL PRIMARY KEY,
    platform_name VARCHAR(50) NOT NULL,
    platform_type VARCHAR(50) NOT NULL
);

CREATE TABLE government_agencies (
  agency_id INT PRIMARY KEY,
  agency_name VARCHAR(100) NOT NULL
);

CREATE TABLE social_media_accounts (
  account_id INT PRIMARY KEY,
  account_name VARCHAR(100) NOT NULL,
  followers_count INT,
  posts_count INT
);

CREATE TABLE election_data (
  election_id INT PRIMARY KEY,
  election_name VARCHAR(100) NOT NULL,
  election_date DATE
);

CREATE TABLE social_media_influence (
  influence_id INT PRIMARY KEY,
  account_id INT,
  election_id INT,
  influence_score DECIMAL(5,2)
);

CREATE TABLE Government_Agencies (
    agency_id INT PRIMARY KEY,
    agency_name VARCHAR(50) NOT NULL,
    agency_type VARCHAR(50) NOT NULL
);

CREATE TABLE Social_Media_Platforms (
    platform_id INT PRIMARY KEY,
    platform_name VARCHAR(50) NOT NULL,
    platform_type VARCHAR(50) NOT NULL
);

CREATE TABLE Elections (
    election_id INT PRIMARY KEY,
    election_name VARCHAR(50) NOT NULL,
    election_year INT NOT NULL
);
