tables.table_schema:
CREATE TABLE analysts (
    analyst_id UUID PRIMARY KEY,
    analyst_name VARCHAR(255),
    analyst_specialization VARCHAR(255)
);

CREATE TABLE social_media_data (
    data_id UUID PRIMARY KEY,
    post_text TEXT,
    post_date TIMESTAMP,
    sentiment_score DECIMAL
);

CREATE TABLE users (
    user_id INT PRIMARY KEY,
    username VARCHAR(50) NOT NULL,
    email VARCHAR(100) NOT NULL
);

CREATE TABLE tweets (
    tweet_id INT PRIMARY KEY,
    content TEXT NOT NULL,
    user_id INT,
    date_posted TIMESTAMP
);

CREATE TABLE sentiment_analysis (
    analysis_id INT PRIMARY KEY,
    tweet_id INT,
    positive_sentiment BOOLEAN,
    negative_sentiment BOOLEAN
);

CREATE TABLE public.twitter_users (
   user_id INT PRIMARY KEY,
   user_name VARCHAR(50),
   user_location VARCHAR(100)
);

CREATE TABLE public.tweets (
   tweet_id INT PRIMARY KEY,
   user_id INT REFERENCES public.twitter_users(user_id),
   tweet_text TEXT,
   tweet_date DATE
);

CREATE TABLE public.sentiment_analysis (
   tweet_id INT REFERENCES public.tweets(tweet_id),
   sentiment_score FLOAT,
   sentiment_category VARCHAR(20)
);

CREATE TABLE public.analysis_results (
   analysis_id INT PRIMARY KEY,
   tweet_id INT REFERENCES public.tweets(tweet_id),
   analyst_name VARCHAR(50),
   analysis_date DATE
);

CREATE TABLE PoliticalAnalysts (
    analyst_id INT,
    analyst_name VARCHAR(50),
    analyst_role VARCHAR(50),
    PRIMARY KEY (analyst_id)
);
