tables.table_schema:
CREATE TABLE Accounts(
    account_id INT PRIMARY KEY,
    username VARCHAR(50) NOT NULL,
    email VARCHAR(100) NOT NULL,
    is_fake BOOLEAN NOT NULL
);

CREATE TABLE flagged_accounts (
    account_id INT PRIMARY KEY,
    reason TEXT,
    flag_date TIMESTAMP
);

CREATE TABLE propaganda_links (
    link_id INT PRIMARY KEY,
    post_id INT,
    link_url VARCHAR(255),
    created_date TIMESTAMP
);

CREATE TABLE propaganda_accounts (
    account_id INT PRIMARY KEY,
    account_name VARCHAR(50) NOT NULL,
    follower_count INT,
    creation_date DATE
);

CREATE TABLE posts (
    post_id INT PRIMARY KEY,
    account_id INT REFERENCES propaganda_accounts(account_id),
    post_content TEXT,
    post_date TIMESTAMP
);

CREATE TABLE hashtags (
    hashtag_id INT PRIMARY KEY,
    hashtag_text VARCHAR(50) UNIQUE,
    post_id INT REFERENCES posts(post_id)
);
