tables.table_schema:
CREATE TABLE customers_feedback (
    customer_id INT PRIMARY KEY,
    customer_name VARCHAR(255),
    feedback_text TEXT,
    rating INT
);

CREATE TABLE content_strategies (
    strategy_id INT PRIMARY KEY,
    strategy_name VARCHAR(255),
    strategy_description TEXT,
    implementation_status BOOLEAN
);

CREATE TABLE CustomerFeedback (
  feedback_id INT PRIMARY KEY,
  customer_id INT,
  feedback_text TEXT,
  feedback_date DATE
);

CREATE TABLE CustomerReviews (
  review_id INT PRIMARY KEY,
  customer_id INT,
  review_text TEXT,
  review_date DATE
);

CREATE TABLE ContentImprovement (
  improvement_id INT PRIMARY KEY,
  feedback_id INT,
  improvement_text TEXT,
  improvement_date DATE
);

CREATE TABLE FeedbackAnalytics (
  analytics_id INT PRIMARY KEY,
  feedback_id INT,
  analytics_data JSONB
);

CREATE TABLE customers (
    customer_id INT PRIMARY KEY,
    customer_name VARCHAR(255) NOT NULL,
    email VARCHAR(255) NOT NULL
);

CREATE TABLE feedback (
    feedback_id INT PRIMARY KEY,
    customer_id INT REFERENCES customers(customer_id),
    feedback_text TEXT NOT NULL,
    feedback_rating INT NOT NULL
);

CREATE TABLE reviews (
    review_id INT PRIMARY KEY,
    feedback_id INT REFERENCES feedback(feedback_id),
    review_text TEXT NOT NULL,
    review_date DATE NOT NULL
);

CREATE TABLE content_strategies (
    strategy_id INT PRIMARY KEY,
    strategy_name VARCHAR(255) NOT NULL,
    strategy_description TEXT
);

CREATE TABLE strategy_feedback (
    strategy_id INT REFERENCES content_strategies(strategy_id),
    feedback_id INT REFERENCES feedback(feedback_id),
    PRIMARY KEY (strategy_id, feedback_id)
);
