CREATE TABLE A/B_test_results (
    test_id INT PRIMARY KEY,
    test_name VARCHAR(255) NOT NULL,
    test_variant VARCHAR(50) NOT NULL,
    test_date DATE
);

CREATE TABLE test_variations (
    variation_id INT PRIMARY KEY,
    test_id INT,
    variation_name VARCHAR(255),
    is_winner BOOLEAN
);
