tables.table_schema:
CREATE TABLE user_activity (
    user_id INT,
    platform VARCHAR,
    activity_type VARCHAR,
    activity_date DATE
);

CREATE TABLE social_media_platform (
    platform_id INT,
    platform_name VARCHAR
);

CREATE TABLE user_profiles (user_id INT PRIMARY KEY, username VARCHAR(50), platform VARCHAR(50), followers INT, activity_count INT);
CREATE TABLE posts (post_id INT PRIMARY KEY, user_id INT, platform VARCHAR(50), post_text TEXT, likes INT, comments INT, post_date DATE);
CREATE TABLE interactions (interaction_id INT PRIMARY KEY, post_id INT, user_id INT, interaction_type VARCHAR(50), interaction_date TIMESTAMP);

CREATE TABLE users (
    user_id INT PRIMARY KEY,
    username VARCHAR(50),
    platform VARCHAR(50),
    registration_date DATE
);

CREATE TABLE likes (
    like_id INT PRIMARY KEY,
    post_id INT,
    user_id INT,
    like_date TIMESTAMP
);
