tables.table_schema:
CREATE TABLE competitor_social_media_accounts (
    account_id INT PRIMARY KEY,
    account_name VARCHAR(255),
    follower_count INT,
    post_count INT
);

CREATE TABLE social_media_posts (
    post_id INT PRIMARY KEY,
    account_id INT,
    post_content TEXT,
    post_date TIMESTAMP
);

CREATE TABLE competitors_activity (
    competitor_id UUID PRIMARY KEY,
    social_media_platform VARCHAR(50),
    post_content TEXT,
    post_date TIMESTAMP
);
