CREATE TABLE email_tracking (
  email_id INT NOT NULL,
  open_rate FLOAT NOT NULL,
  click_through_rate FLOAT NOT NULL,
  conversion_rate FLOAT NOT NULL
);

CREATE TABLE email_open_rates (
    email_id INT,
    open_timestamp TIMESTAMP,
    user_id INT
);

CREATE TABLE click_through_rates (
    email_id INT,
    click_timestamp TIMESTAMP,
    user_id INT
);

CREATE TABLE email_open_rates (
    email_id STRING PRIMARY KEY,
    open_time TIMESTAMP,
    recipient_email STRING
);

CREATE TABLE click_through_rates (
    click_id STRING PRIMARY KEY,
    click_time TIMESTAMP,
    email_id STRING,
    recipient_email STRING
);

CREATE TABLE conversion_rates (
    conversion_id STRING PRIMARY KEY,
    conversion_time TIMESTAMP,
    email_id STRING,
    recipient_email STRING
);

CREATE TABLE email_open_rates (
  email_id UUID PRIMARY KEY,
  open_timestamp TIMESTAMP,
  user_id UUID
);

CREATE TABLE click_through_rates (
  click_id UUID PRIMARY KEY,
  click_timestamp TIMESTAMP,
  user_id UUID
);

CREATE TABLE conversion_rates (
  conversion_id UUID PRIMARY KEY,
  conversion_timestamp TIMESTAMP,
  user_id UUID
);

CREATE TABLE user_info (
  user_id UUID PRIMARY KEY,
  email_address VARCHAR,
  registration_timestamp TIMESTAMP
);

CREATE TABLE EmailCampaigns (
    campaign_id INT PRIMARY KEY,
    campaign_name VARCHAR(255),
    start_date DATE,
    end_date DATE
);

CREATE TABLE EmailOpens (
    open_id INT PRIMARY KEY,
    campaign_id INT,
    open_date TIMESTAMP,
    IP_address VARCHAR(255)
);

CREATE TABLE EmailClicks (
    click_id INT PRIMARY KEY,
    campaign_id INT,
    click_date TIMESTAMP,
    clicked_link VARCHAR(255)
);

CREATE TABLE Conversions (
    conversion_id INT PRIMARY KEY,
    campaign_id INT,
    conversion_date TIMESTAMP,
    converted_to VARCHAR(255)
);

CREATE TABLE CampaignMetrics (
    metric_id INT PRIMARY KEY,
    campaign_id INT,
    open_rate DECIMAL,
    click_through_rate DECIMAL,
    conversion_rate DECIMAL
);
