tables.table_schema:
CREATE TABLE ab_testing_campaigns (
    campaign_id UUID PRIMARY KEY,
    subject_line VARCHAR(255),
    content TEXT,
    design TEXT
);

CREATE TABLE EmailSubjects (
    id SERIAL PRIMARY KEY,
    subject_line VARCHAR(255) NOT NULL,
    campaign_id INT NOT NULL,
    group_id INT NOT NULL
);

CREATE TABLE CampaignMetrics (
    id SERIAL PRIMARY KEY,
    campaign_id INT NOT NULL,
    emails_sent INT NOT NULL,
    opens INT NOT NULL,
    clicks INT NOT NULL
);

CREATE TABLE email_subject_lines (
  id INT PRIMARY KEY,
  subject_line TEXT
);

CREATE TABLE email_content (
  id INT PRIMARY KEY,
  content TEXT
);

CREATE TABLE email_designs (
  id INT PRIMARY KEY,
  design_name TEXT
);

CREATE TABLE email_subject_lines (
    subject_id INT PRIMARY KEY,
    subject_line VARCHAR
);

CREATE TABLE email_content (
    content_id INT PRIMARY KEY,
    content_text TEXT
);

CREATE TABLE email_designs (
    design_id INT PRIMARY KEY,
    design_description VARCHAR
);

CREATE TABLE designs (
    design_id INT PRIMARY KEY,
    design_name VARCHAR(255),
    design_image_path TEXT
);
