tables.table_schema:
CREATE TABLE Influencer (
    influencer_id INT PRIMARY KEY,
    influencer_name VARCHAR(50),
    followers_count INT
);

CREATE TABLE followers(
  follower_id INT PRIMARY KEY,
  follower_name VARCHAR(255),
  age INT,
  location VARCHAR(255)
);

CREATE TABLE influencer(
  influencer_id INT PRIMARY KEY,
  influencer_name VARCHAR(255),
  niche VARCHAR(255),
  engagement_rate FLOAT
);

CREATE TABLE follower_demographics(
  follower_id INT,
  gender VARCHAR(10),
  interests TEXT,
  FOREIGN KEY (follower_id) REFERENCES followers(follower_id)
);

CREATE TABLE influencers (
    influencer_id INT PRIMARY KEY,
    influencer_name VARCHAR(50),
    influencer_category VARCHAR(50)
);
