tables.table_schema:
CREATE TABLE brand (
    brand_id INT PRIMARY KEY,
    brand_name TEXT,
    industry TEXT,
    target_audience TEXT
);

CREATE TABLE influencer (
    influencer_id INT PRIMARY KEY,
    influencer_name TEXT,
    follower_count INT,
    audience_demographics TEXT
);

CREATE TABLE brands (
    id INT PRIMARY KEY,
    name VARCHAR(255),
    industry VARCHAR(100),
    website VARCHAR(255)
);

CREATE TABLE brand_influencer_matches (
    match_id UUID PRIMARY KEY,
    brand_id UUID REFERENCES brands(brand_id),
    influencer_id UUID REFERENCES influencers(influencer_id),
    match_score DECIMAL
);

CREATE TABLE Brand (
    brand_id INT PRIMARY KEY,
    brand_name VARCHAR(50) NOT NULL,
    industry VARCHAR(50) NOT NULL
);

CREATE TABLE Influencer (
    influencer_id INT PRIMARY KEY,
    influencer_name VARCHAR(50) NOT NULL,
    followers_count INT NOT NULL,
    engagement_rate FLOAT
);

CREATE TABLE Audience (
    audience_id INT PRIMARY KEY,
    gender VARCHAR(10),
    age_group VARCHAR(20),
    location VARCHAR(50)
);

CREATE TABLE BrandAudienceMatch (
    brand_id INT,
    audience_id INT,
    FOREIGN KEY (brand_id) REFERENCES Brand(brand_id),
    FOREIGN KEY (audience_id) REFERENCES Audience(audience_id)
);

CREATE TABLE InfluencerAudienceMatch (
    influencer_id INT,
    audience_id INT,
    FOREIGN KEY (influencer_id) REFERENCES Influencer(influencer_id),
    FOREIGN KEY (audience_id) REFERENCES Audience(audience_id)
);
