tables.table_schema:
CREATE TABLE SocialMediaPlatform (
    platform_id INT,
    platform_name VARCHAR(50),
    followers INT,
    engagement_rate DECIMAL(5,2)
);

CREATE TABLE brand_info (
    brand_id INT PRIMARY KEY,
    brand_name VARCHAR(255),
    industry VARCHAR(255),
    social_media_handles VARCHAR(255)
);

CREATE TABLE influencer (
    influencer_id INT PRIMARY KEY,
    influencer_name VARCHAR(255),
    follower_count INT,
    social_media_platform VARCHAR(50)
);

CREATE TABLE Brand (brand_id INT, brand_name VARCHAR(255), industry VARCHAR(255));
CREATE TABLE Influencer (influencer_id INT, influencer_name VARCHAR(255), followers_count INT, platform VARCHAR(255));
CREATE TABLE Content (content_id INT, content_description TEXT, post_date DATE, influencer_id INT);
CREATE TABLE SocialMediaPlatform (platform_id INT, platform_name VARCHAR(255));
CREATE TABLE ContentEngagement (engagement_id INT, content_id INT, likes INT, comments INT, shares INT);
