tables.table_schema:
CREATE TABLE Influencers (
    influencer_id INT PRIMARY KEY,
    influencer_name VARCHAR(255),
    email VARCHAR(255)
);

CREATE TABLE Brands (
    brand_id INT PRIMARY KEY,
    brand_name VARCHAR(255),
    industry VARCHAR(255)
);

CREATE TABLE influencer_content (
    id UUID PRIMARY KEY,
    influencer_id UUID NOT NULL,
    content TEXT NOT NULL,
    post_date TIMESTAMP NOT NULL
);

CREATE TABLE brands (
    id UUID PRIMARY KEY,
    brand_name TEXT NOT NULL,
    industry TEXT NOT NULL,
    website TEXT
);

CREATE TABLE marketing_strategies (
    id UUID PRIMARY KEY,
    strategy_name TEXT NOT NULL,
    description TEXT,
    start_date DATE NOT NULL
);

CREATE TABLE repurposed_content (
    id UUID PRIMARY KEY,
    original_content_id UUID NOT NULL,
    repurposed_content TEXT NOT NULL,
    repurpose_date TIMESTAMP NOT NULL
);
