tables.table_schema:
CREATE TABLE influencers (
    id INT PRIMARY KEY,
    name VARCHAR(255),
    followers INT,
    category VARCHAR(50)
);

CREATE TABLE influencers (
    id INT PRIMARY KEY,
    name TEXT,
    followers INT
);

CREATE TABLE products (
    id INT PRIMARY KEY,
    product_name TEXT,
    category TEXT,
    unit_price DECIMAL(10,2)
);

CREATE TABLE influencer_campaigns (
    influencer_id INT,
    campaign_id INT,
    FOREIGN KEY (influencer_id) REFERENCES influencers(id),
    FOREIGN KEY (campaign_id) REFERENCES campaigns(id)
);
