tables.table_schema:
CREATE TABLE leads (
  lead_id INT PRIMARY KEY,
  lead_name VARCHAR(50),
  lead_score INT
);

CREATE TABLE lead_scoring_rules (
  rule_id INT PRIMARY KEY,
  rule_name VARCHAR(50),
  scoring_criteria VARCHAR(100),
  weightage DECIMAL
);

CREATE TABLE lead (
  lead_id UUID PRIMARY KEY,
  lead_name VARCHAR(50),
  lead_email VARCHAR(100),
  lead_score INT
);

CREATE TABLE lead_interaction (
  interaction_id UUID PRIMARY KEY,
  lead_id UUID REFERENCES lead(lead_id),
  interaction_type VARCHAR(50),
  interaction_date TIMESTAMPTZ
);

CREATE TABLE lead_scoring_rules (
  rule_id UUID PRIMARY KEY,
  rule_name VARCHAR(50),
  rule_description TEXT,
  rule_score INT
);

CREATE TABLE lead_priority (
  priority_id UUID PRIMARY KEY,
  lead_id UUID REFERENCES lead(lead_id),
  priority_level VARCHAR(20),
  priority_date TIMESTAMPTZ
);

CREATE TABLE Leads (
    lead_id INT PRIMARY KEY,
    lead_name VARCHAR(255),
    lead_email VARCHAR(255),
    lead_score INT
);

CREATE TABLE LeadActivities (
    activity_id INT PRIMARY KEY,
    lead_id INT,
    activity_type VARCHAR(100),
    activity_date TIMESTAMP
);

CREATE TABLE LeadScoreWeights (
    weight_id INT PRIMARY KEY,
    activity_type VARCHAR(100),
    weight INT
);

CREATE TABLE SalesTeam (
    salesperson_id INT PRIMARY KEY,
    salesperson_name VARCHAR(255),
    team_lead BOOLEAN
);

CREATE TABLE LeadScores (
    lead_id INT PRIMARY KEY,
    lead_score INT,
    last_update TIMESTAMP
);
