tables.table_schema:
CREATE TABLE subscription_lists (
    list_id INT PRIMARY KEY,
    list_name VARCHAR(255) NOT NULL,
    list_description TEXT
);

CREATE TABLE newsletter_subscriptions (
    subscription_id INT PRIMARY KEY,
    email VARCHAR(255) NOT NULL,
    customer_id INT NOT NULL,
    subscription_date DATE NOT NULL
);

CREATE TABLE customer_segments (
    segment_id INT PRIMARY KEY,
    segment_name VARCHAR(100) NOT NULL,
    criteria JSONB NOT NULL
);

CREATE TABLE newsletter_content (
    content_id INT PRIMARY KEY,
    segment_id INT NOT NULL,
    content TEXT NOT NULL,
    send_date TIMESTAMP
);

CREATE TABLE subscription_list (
    list_id INT PRIMARY KEY,
    list_name TEXT
);

CREATE TABLE customer (
    customer_id INT PRIMARY KEY,
    first_name TEXT,
    last_name TEXT,
    email TEXT
);

CREATE TABLE newsletter (
    newsletter_id INT PRIMARY KEY,
    subject TEXT,
    content TEXT,
    created_date DATE
);

CREATE TABLE segment (
    segment_id INT PRIMARY KEY,
    segment_name TEXT,
    criteria TEXT
);

CREATE TABLE subscribers (
    subscriber_id INT PRIMARY KEY,
    email VARCHAR(255) UNIQUE NOT NULL,
    name VARCHAR(100) NOT NULL,
    subscription_date DATE
);

CREATE TABLE newsletters (
    newsletter_id INT PRIMARY KEY,
    title VARCHAR(255) NOT NULL,
    content TEXT NOT NULL,
    sent_date DATE
);

CREATE TABLE segments (
    segment_id INT PRIMARY KEY,
    segment_name VARCHAR(100) NOT NULL
);

CREATE TABLE subscriber_segment (
    subscriber_id INT REFERENCES subscribers(subscriber_id),
    segment_id INT REFERENCES segments(segment_id),
    PRIMARY KEY (subscriber_id, segment_id)
);

CREATE TABLE segment_newsletter (
    segment_id INT REFERENCES segments(segment_id),
    newsletter_id INT REFERENCES newsletters(newsletter_id),
    PRIMARY KEY (segment_id, newsletter_id)
);
