tables.table_schema:
CREATE TABLE customers (
    customer_id INT PRIMARY KEY,
    location VARCHAR(50),
    age INT,
    preferences VARCHAR(100)
);

CREATE TABLE marketing_segments (
    segment_id INT PRIMARY KEY,
    segment_name VARCHAR(255),
    segment_criteria VARCHAR(255)
);

CREATE TABLE CustomerLocation (
  customer_id UUID PRIMARY KEY,
  city VARCHAR(255) NOT NULL,
  state VARCHAR(255) NOT NULL,
  country VARCHAR(255) NOT NULL
);

CREATE TABLE CustomerAge (
  customer_id UUID PRIMARY KEY,
  age INT NOT NULL,
  birthdate DATE NOT NULL
);

CREATE TABLE CustomerPreference (
  customer_id UUID PRIMARY KEY,
  brand_preference VARCHAR(255) NOT NULL,
  communication_preference VARCHAR(255) NOT NULL
);

CREATE TABLE locations (
    location_id UUID PRIMARY KEY,
    city VARCHAR(50),
    state VARCHAR(50),
    country VARCHAR(50)
);

CREATE TABLE age_groups (
    group_id UUID PRIMARY KEY,
    min_age INT,
    max_age INT,
    description VARCHAR(100)
);

CREATE TABLE location_segments (
    segment_id UUID PRIMARY KEY,
    segment_name STRING,
    location STRING
);

CREATE TABLE age_segments (
    segment_id UUID PRIMARY KEY,
    segment_name STRING,
    min_age INT,
    max_age INT
);

CREATE TABLE preferences_segments (
    segment_id UUID PRIMARY KEY,
    segment_name STRING,
    preferences STRING
);
