tables.table_schema:
CREATE TABLE marketing_messages (
    message_id UUID PRIMARY KEY,
    message_text VARCHAR,
    design_id UUID,
    timestamp TIMESTAMP
);

CREATE TABLE brand_messages (
  id INT PRIMARY KEY,
  message TEXT,
  design TEXT,
  variant INT
);

CREATE TABLE testing_groups (
  group_id INT PRIMARY KEY,
  group_name TEXT,
  group_description TEXT
);

CREATE TABLE MarketingMessages (
  message_id INT PRIMARY KEY,
  message_text TEXT,
  message_design TEXT,
  message_type TEXT
);

CREATE TABLE ATestGroup (
  group_id INT PRIMARY KEY,
  message_id INT REFERENCES MarketingMessages(message_id),
  test_date DATE
);

CREATE TABLE BTestGroup (
  group_id INT PRIMARY KEY,
  message_id INT REFERENCES MarketingMessages(message_id),
  test_date DATE
);

CREATE TABLE TestResults (
  result_id INT PRIMARY KEY,
  group_id INT REFERENCES ATestGroup(group_id),
  conversion_rate DECIMAL
);

CREATE TABLE TestFeedback (
  feedback_id INT PRIMARY KEY,
  result_id INT REFERENCES TestResults(result_id),
  feedback_text TEXT
);
