tables.table_schema:
CREATE TABLE BrandMetrics (
    brand_id INT,
    awareness_score INT,
    reach_score INT,
    engagement_score INT
);

CREATE TABLE BrandAwarenessReports (
  id INT PRIMARY KEY,
  brand_name TEXT NOT NULL,
  reach_metrics JSONB,
  engagement_metrics JSONB
);

CREATE TABLE CustomerEngagementReports (
  id INT PRIMARY KEY,
  customer_id INT NOT NULL,
  engagement_rating FLOAT,
  engagement_timestamp TIMESTAMP
);

CREATE TABLE BrandAwarenessMetrics (
    id INT PRIMARY KEY,
    brand_name VARCHAR(255),
    awareness_score INT,
    date_recorded DATE
);

CREATE TABLE ReachMetrics (
    id INT PRIMARY KEY,
    channel_name VARCHAR(255),
    reach_count INT,
    date_recorded DATE
);

CREATE TABLE CustomerEngagementMetrics (
    id INT PRIMARY KEY,
    customer_id INT,
    engagement_level VARCHAR(50),
    date_recorded DATE
);

CREATE TABLE BrandMetrics (
   brand_id INT PRIMARY KEY,
   brand_name VARCHAR(255),
   reach_metrics INT,
   engagement_metrics INT
);

CREATE TABLE CustomerEngagement (
   customer_id INT PRIMARY KEY,
   customer_name VARCHAR(255),
   email VARCHAR(255),
   engagement_score INT
);
