tables.table_schema:
CREATE TABLE participants (
    participant_id INT PRIMARY KEY,
    name VARCHAR(255),
    email VARCHAR(255),
    preference VARCHAR(255)
);

CREATE TABLE attendees (
 attendee_id UUID PRIMARY KEY,
 first_name VARCHAR(50),
 last_name VARCHAR(50),
 email VARCHAR(100)
);

CREATE TABLE events (
 event_id UUID PRIMARY KEY,
 event_name VARCHAR(100),
 event_date TIMESTAMP,
 location VARCHAR(100)
);

CREATE TABLE preferences (
 preference_id UUID PRIMARY KEY,
 attendee_id UUID REFERENCES attendees(attendee_id),
 preference VARCHAR(50)
);

CREATE TABLE attendee_preferences (
    attendee_id INT,
    preference_category VARCHAR(50),
    preference_value VARCHAR(100)
);

CREATE TABLE invitation_status (
    attendee_id INT,
    event_id INT,
    invitation_sent BOOLEAN,
    rsvp_status VARCHAR(20)
);
