tables.table_schema:
CREATE TABLE registrations (
    registration_id SERIAL PRIMARY KEY,
    event_id INT REFERENCES events(event_id),
    attendee_id INT REFERENCES event_attendees(attendee_id),
    registration_date TIMESTAMP
);

CREATE TABLE attendees (
    id SERIAL PRIMARY KEY,
    name VARCHAR(50) NOT NULL,
    email VARCHAR(100) UNIQUE NOT NULL,
    phone VARCHAR(15) NOT NULL
);

CREATE TABLE events (
    id SERIAL PRIMARY KEY,
    event_name VARCHAR(100) NOT NULL,
    start_date DATE NOT NULL, 
    end_date DATE NOT NULL
);

CREATE TABLE registrations (
    registration_id SERIAL PRIMARY KEY,
    attendee_id INT REFERENCES attendees(attendee_id),
    event_id INT REFERENCES events(event_id),
    registration_date DATE NOT NULL
);

CREATE TABLE event_organizers (
    organizer_id SERIAL PRIMARY KEY,
    organizer_name VARCHAR(100) NOT NULL,
    organizer_email VARCHAR(100) NOT NULL,
    organizer_phone_number VARCHAR(20)
);

CREATE TABLE event_organizer_assignments (
    assignment_id SERIAL PRIMARY KEY,
    organizer_id INT REFERENCES event_organizers(organizer_id),
    event_id INT REFERENCES events(event_id),
    assignment_date DATE NOT NULL
);
