tables.table_schema:
CREATE TABLE customers (
    id UUID PRIMARY KEY,
    name STRING,
    email STRING,
    phone_number STRING
);

CREATE TABLE marketing_campaigns (
    id UUID PRIMARY KEY,
    campaign_name STRING,
    campaign_date DATE,
    location STRING
);

CREATE TABLE campaigns (
    campaign_id INT,
    campaign_name VARCHAR(100),
    campaign_date DATE, 
    PRIMARY KEY (campaign_id)
);
