CREATE TABLE customers (
    customer_id INT PRIMARY KEY,
    name VARCHAR(50),
    response_to_tactic INT
);

CREATE TABLE tactics (
    tactic_id INT PRIMARY KEY,
    tactic_name VARCHAR(50),
    tactic_description TEXT
);

CREATE TABLE customers (
    customer_id INT PRIMARY KEY,
    name VARCHAR(50),
    email VARCHAR(100),
    response_to_guerrilla_tactics BOOLEAN
);

CREATE TABLE segments (
    segment_id INT PRIMARY KEY,
    segment_name VARCHAR(50),
    description TEXT
);

CREATE TABLE customer_segments (
    customer_id INT,
    segment_id INT,
    FOREIGN KEY (customer_id) REFERENCES customers(customer_id),
    FOREIGN KEY (segment_id) REFERENCES segments(segment_id)
);

CREATE TABLE tactics (
    tactic_id INT PRIMARY KEY,
    tactic_name VARCHAR(50),
    tactic_description TEXT,
    cost DECIMAL(10, 2)
);

CREATE TABLE Customers (
  customer_id INT PRIMARY KEY,
  customer_name VARCHAR(50) NOT NULL,
  guerrilla_marketing_response BOOLEAN
);

CREATE TABLE MarketingTactics (
  tactic_id INT PRIMARY KEY,
  tactic_name VARCHAR(50) NOT NULL,
  tactic_description TEXT
);

CREATE TABLE CustomerSegmentation (
  segmentation_id INT PRIMARY KEY,
  segment_name VARCHAR(50) NOT NULL,
  segment_description TEXT
);

CREATE TABLE CustomerTactics (
  customer_id INT,
  tactic_id INT,
  FOREIGN KEY (customer_id) REFERENCES Customers(customer_id),
  FOREIGN KEY (tactic_id) REFERENCES MarketingTactics(tactic_id)
);

CREATE TABLE CustomerSegments (
  customer_id INT,
  segmentation_id INT,
  FOREIGN KEY (customer_id) REFERENCES Customers(customer_id),
  FOREIGN KEY (segmentation_id) REFERENCES CustomerSegmentation(segmentation_id)
);
