tables.table_schema:
CREATE TABLE campaign_budgets (
  campaign_id UUID PRIMARY KEY,
  campaign_name VARCHAR(255),
  budget DECIMAL(10, 2)
);

CREATE TABLE guerrilla_marketing_campaigns (
    id INT PRIMARY KEY,
    campaign_name VARCHAR(255),
    start_date DATE,
    end_date DATE
);

CREATE TABLE campaign_budgets (
    id INT PRIMARY KEY,
    campaign_id INT,
    budget_amount DECIMAL,
    budget_year INT
);

CREATE TABLE campaign_expenses (
    id INT PRIMARY KEY,
    campaign_id INT,
    expense_amount DECIMAL,
    expense_date DATE
);
