tables.table_schema:
CREATE TABLE guerrilla_marketing_influencers (
    influencer_id INT PRIMARY KEY,
    influencer_name VARCHAR(255),
    followers_count INT,
    collaboration_date DATE
);

CREATE TABLE Influencers (
    influencer_id INT PRIMARY KEY,
    influencer_name VARCHAR(255) NOT NULL,
    follower_count INT,
    contact_email VARCHAR(255)
);

CREATE TABLE Companies (
    company_id INT PRIMARY KEY,
    company_name VARCHAR(255) NOT NULL,
    industry VARCHAR(100)
);

CREATE TABLE Payments (
    payment_id INT PRIMARY KEY,
    collaboration_id INT,
    payment_amount FLOAT,
    payment_date DATE
);

CREATE TABLE guerrilla_marketing_influencers (
  influencer_id UUID PRIMARY KEY,
  influencer_name VARCHAR(255) NOT NULL,
  social_media_handle VARCHAR(100) NOT NULL
);

CREATE TABLE influencer_followers (
  follower_id UUID PRIMARY KEY,
  influencer_id UUID,
  follower_count INT NOT NULL
);

CREATE TABLE brand_accounts (
  brand_id UUID PRIMARY KEY,
  brand_name VARCHAR(255) NOT NULL,
  brand_category VARCHAR(100) NOT NULL
);
