tables.table_schema:
CREATE TABLE affiliate_accounts (
    account_id INT PRIMARY KEY,
    account_name VARCHAR(255) NOT NULL,
    affiliate_code VARCHAR(10) UNIQUE
);

CREATE TABLE payment_history (
    payment_id INT PRIMARY KEY,
    account_id INT REFERENCES affiliate_accounts(account_id),
    payment_amount DECIMAL(10, 2),
    payment_date DATE
);

CREATE TABLE affiliates (
    affiliate_id INT PRIMARY KEY,
    affiliate_name VARCHAR(255),
    email VARCHAR(255),
    join_date DATE
);

CREATE TABLE campaigns (
    campaign_id INT PRIMARY KEY,
    campaign_name VARCHAR(255),
    start_date DATE,
    end_date DATE
);

CREATE TABLE clicks (
    click_id INT PRIMARY KEY,
    affiliate_id INT,
    campaign_id INT,
    click_timestamp TIMESTAMP
);

CREATE TABLE conversions (
    conversion_id INT PRIMARY KEY,
    click_id INT,
    conversion_amount DECIMAL(10, 2),
    conversion_date DATE
);

CREATE TABLE affiliates (
  affiliate_id INT PRIMARY KEY,
  username VARCHAR(50),
  email VARCHAR(100),
  join_date DATE
);

CREATE TABLE conversions (
  conversion_id INT PRIMARY KEY,
  click_id INT,
  conversion_date DATE,
  revenue DECIMAL(10, 2)
);

CREATE TABLE payouts (
  payout_id INT PRIMARY KEY,
  conversion_id INT,
  payout_date DATE,
  amount DECIMAL(10, 2)
);
