tables.table_schema:
CREATE TABLE affiliate_partnerships (
    partnership_id INT PRIMARY KEY,
    partner_name VARCHAR(255),
    start_date DATE,
    commission_rate DECIMAL(5,2)
);

CREATE TABLE affiliate_partnerships (
    partnership_id INT PRIMARY KEY,
    partner_name TEXT,
    partner_website TEXT,
    commission_rate DECIMAL
);

CREATE TABLE optimization_reports (
    report_id INT PRIMARY KEY,
    partnership_id INT,
    optimization_description TEXT,
    recommended_actions TEXT
);

CREATE TABLE partners (
    partner_id UUID PRIMARY KEY,
    partner_name VARCHAR(255) NOT NULL,
    partner_email VARCHAR(255) UNIQUE NOT NULL
);

CREATE TABLE campaigns (
    campaign_id UUID PRIMARY KEY,
    campaign_name VARCHAR(255),
    start_date DATE NOT NULL,
    end_date DATE NOT NULL
);

CREATE TABLE conversions (
    conversion_id UUID PRIMARY KEY,
    campaign_id UUID NOT NULL,
    conversion_date DATE NOT NULL
);

CREATE TABLE clicks (
    click_id UUID PRIMARY KEY,
    partner_id UUID NOT NULL,
    click_timestamp TIMESTAMP NOT NULL
);

CREATE TABLE reports (
    report_id UUID PRIMARY KEY,
    campaign_id UUID NOT NULL,
    partner_id UUID NOT NULL,
    report_date DATE NOT NULL
);
