tables.table_schema:
CREATE TABLE ClickThroughRates (
    id INT PRIMARY KEY,
    affiliate_id INT,
    link_id INT,
    click_date DATE
);

CREATE TABLE Conversions (
    id INT PRIMARY KEY,
    affiliate_id INT,
    conversion_date DATE,
    conversion_amount DECIMAL
);

CREATE TABLE Revenue (
    id INT PRIMARY KEY,
    affiliate_id INT,
    revenue_date DATE,
    revenue_amount DECIMAL
);

CREATE TABLE AffiliateLinks (
    id INT PRIMARY KEY,
    affiliate_id INT,
    link_url VARCHAR(255)
);
