tables.table_schema:
CREATE TABLE AudienceSegmentation (
    id SERIAL PRIMARY KEY,
    demographic TEXT,
    interests TEXT,
    buying_behavior TEXT
);

CREATE TABLE affiliates (
  affiliate_id INT PRIMARY KEY,
  affiliate_name TEXT NOT NULL,
  affiliate_email TEXT UNIQUE,
  affiliate_location TEXT
);

CREATE TABLE demographics (
    user_id INT PRIMARY KEY,
    age INT,
    gender VARCHAR,
    location VARCHAR
);

CREATE TABLE interests (
    user_id INT,
    interest VARCHAR,
    FOREIGN KEY (user_id) REFERENCES demographics(user_id)
);

CREATE TABLE buying_behavior (
    user_id INT,
    purchase_count INT,
    total_amount DECIMAL,
    FOREIGN KEY (user_id) REFERENCES demographics(user_id)
);

CREATE TABLE campaigns (
    campaign_id INT PRIMARY KEY,
    campaign_name VARCHAR,
    start_date DATE,
    end_date DATE
);
